step.do:
${load_modules};
        ${export}; 

        rm -r -f ${cover_install_folder}
        if [ ! -d "${cover_repo_folder}" ]; then git clone ${cover_git_clone_ssh} ${cover_repo_folder}; fi;
        cd ${cover_repo_folder};
        if ! git cat-file -t ${cover_commit}; then git pull; fi;
        git reset --hard;
        git clean -fdx;
        git checkout ${cover_commit};
        git submodule update --init --recursive;

        mkdir -p ${cover_build_folder};
        cd ${cover_build_folder};
        cmake ${cover_build_args} ${cover_repo_folder};
        make -j50 install;
        rm -r -f ${cover_build_folder};

        if [ ! -f "${cover_cc_bin}" ]; then >&2 echo "CoVeR does not exist at ${coverbin_path}"; exit 1; fi

${load_modules};
        ${export}; 

        rm -r -f ${must_install_folder}
        if [ ! -d "${must_repo_folder}" ]; then git clone ${must_git_clone_ssh} ${must_repo_folder}; fi;
        cd ${must_repo_folder};
        if ! git cat-file -t ${__must_commit}; then git pull; fi;
        git reset --hard;
        git clean -fdx;
        git checkout ${__must_commit};
        git submodule update --init --recursive;

        mkdir -p ${must_build_folder};
        cd ${must_build_folder};
        cmake ${MUST_build_args} ${must_repo_folder};
        make -j50 install;
        rm -r -f ${must_build_folder};

        if [ ! -f "${MUSTRUN}" ]; then >&2 echo "mustrun does not exist at ${mustbin_path}"; exit 1; fi

${load_modules}; 
        ${export}; 
        ${compile_export};

        rm -r -f build
        mkdir build
        cd build
        CC="${COVER_CC} -include ${cover_install_folder}/include/mpi_contracts.h" CXX="${COVER_CXX} -include ${cover_install_folder}/include/mpi_contracts.h" cmake -DCMAKE_BUILD_TYPE=Debug -DMODEL=serial -DENABLE_MPI=ON ../copy_source/
        make clean;
        ${time_measure_command} make;

        mv ${executable_name} ${jube_wp_abspath}/${executable_full_name}
        cp contract_messages.json ${jube_wp_abspath}

echo "${compile_mode}";
        echo "${compile_common_env_vars}";
        echo "${export}";
        ${load_modules}; 
        ${export}; 
        ${compile_export};

        rm -r -f build
        mkdir build
        cd build
        CC="$MPICC" CXX="$MPICXX" cmake -DCMAKE_BUILD_TYPE=Debug -DMODEL=serial -DENABLE_MPI=ON ../copy_source/
        make clean;
        ${time_measure_command} make;

        mv ${executable_name} ${jube_wp_abspath}/${executable_full_name};
